require_relative 'input'
require 'ostruct'

def segment(input = nil)
  strs = []
  strs << ''.split('')

  strs.each do |str|
    cursor = 1
    str.each_with_index do |_, idx|
      block = str[idx .. idx + 3]
      if block.size == block.uniq.size
        cursor = idx + 4
        break
      end
    end
    pp cursor
  end

end

def segment_msg(input = nil)
  strs = []
  #strs << 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'.split('')
  #strs << 'bvwbjplbgvbhsrlpgdmjqwftvncz'.split('')
  #strs << 'nppdvjthqldpwncqszvftbrmjlhg'.split('')
  #strs << 'nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg'.split('')
  strs << ''.split('')

  strs.each do |str|
    cursor = 1
    str.each_with_index do |_, idx|
      block = str[idx .. idx + 13]
      pp "#{block.join('')} // #{block.size} // #{block.uniq.size}"
      if block.size == block.uniq.size
        cursor = idx + 14
        break
      end
    end
    pp cursor
  end

end

segment
segment_msg

